namespace Nukium\GLS\Common\DTO\HttpClient;

class HeaderParser
{
    protected $headers = [];

    public function __invoke($curl, $header)
    {
        $len = strlen($header);
        $header = explode(':', $header, 2);

        if (count($header) !== 2) {
            return $len;
        }

        $key = strtolower(trim($header[0]));
        $value = trim($header[1]);

        $this->headers[$key] = $value;

        return $len;
    }

    public function getHeaders()
    {
        return $this->headers;
    }
}
