namespace Nukium\GLS\Common\Exception;

class GlsException extends \Exception
{
    public function setMessage($message)
    {
        $this->message = $message;

        return $this;
    }

    public function setCode($code)
    {
        $this->code = (int) $code;

        return $this;
    }
}
