namespace Nukium\GLS\Common\Service\Repository\Cache;

use Nukium\GLS\Common\Exception\LogicException;

class CacheRepositoryFactory
{
    private static $instance = null;

    public static function getInstance()
    {
        if (self::$instance === null) {
            throw new LogicException('You must provide CacheRepositoryInterface instance via CacheRepositoryFactory::setInstance');
        }

        return self::$instance;
    }

    public static function setInstance(CacheRepositoryInterface $instance)
    {
        self::$instance = $instance;
    }
}
