namespace Nukium\PrestaShop\GLS\Service\Adapter\Config;

use Nukium\GLS\Common\Service\Adapter\Config\ConfigInterface;

class PrestashopConfig implements ConfigInterface
{
    private static $instance;

    public static function getInstance()
    {
        if (self::$instance === null) {
            self::$instance = new self();
        }

        return self::$instance;
    }

    public function __construct()
    {
    }

    public function get($name)
    {
        $res = \Configuration::get($name);

        if ($res === false) {
            return null;
        }

        return (string) $res;
    }

    public function update($name, $value)
    {
        return \Configuration::updateValue($name, $value);
    }
}
